module.exports = {
  describe(name, fn) {
    console.log(name);
    fn();
  },
  it(name, fn) {
    try {
      fn();
      console.log(`\u2713 ${name}`);
    } catch (err) {
      console.error(`\u2717 ${name}`);
      console.error(err);
      process.exitCode = 1;
    }
  },
  expect(value) {
    return {
      toBe(expected) {
        if (value !== expected) {
          throw new Error(`Expected ${expected} but got ${value}`);
        }
      }
    };
  }
};
